INSERT INTO `dr_1_product` VALUES (16,11,' Anesthesia Video Laryngoscope','253','','',2,2,'biohermes',9,'/products/show-16.html',0,0,'127.0.0.1-54720',1716887629,1719307162,999,'&lt;p&gt;&lt;b&gt;Model:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;OLT-AVL-X, OLT-AVL-C, OLT-AVL-H&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Application:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;It can be used to provoke the epiglottic part of the patient to expose the glottis, guide the medical staff to accurately perform airway intubation for anesthesia or emergency use, and can also be used for intraoral examination and treatment.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Advantages:&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Can take photos and videos for storage, record classic intubation cases with one click, convenient for teaching and research&lt;/li&gt;&lt;li&gt;The display can be adjusted at any angle multi-angle observation, avoiding blind spot&lt;/li&gt;&lt;li&gt;High-definition video image guidance, more accurate operation and safer anesthesia&lt;/li&gt;&lt;/ul&gt;&lt;div&gt;&lt;br&gt;&lt;/div&gt;','',0,'',''),(17,11,'Disposable Double-Lumen Endobronchial Tube','252','','',1,2,'biohermes',9,'/products/show-17.html',0,0,'127.0.0.1-63180',1716901257,1719307436,500,'&lt;p&gt;&lt;b&gt;Model: &lt;/b&gt;&lt;/p&gt;&lt;p&gt;Visual type Left cavity: 32Fr, 35Fr, 37Fr&lt;/p&gt;&lt;p&gt;Normal type Left cavity: 28Fr, 32Fr, 35Fr, 37Fr, 39Fr, 41Fr&lt;/p&gt;&lt;p&gt;&lt;b&gt;&lt;br&gt;&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Application:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;Double lumen endobronchial tube is placed in the main trunk of the bronchus and can be used for selective inflation or deflation, sputum suction, unilateral lung ventilation, or for bronchoscopy&lt;/p&gt;&lt;p&gt;&lt;b&gt;Advantages:&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;The air bag regulates single and double lung ventilation, and the operation is simple and flexible&lt;/li&gt;&lt;li&gt;High-volume, low-pressure cuff reduces compression damage to the airway&lt;/li&gt;&lt;li&gt;The airbag structure is well fixed, not easy to shift and highly safe&lt;/li&gt;&lt;li&gt;The distal arc-shaped structure has a guiding function and facilitates positioning&lt;/li&gt;&lt;li&gt;Reduce the difficulty of tracheal intubation, dynamically monitor the airway, and improve the safety and effectiveness of anesthesia&lt;/li&gt;&lt;/ul&gt;','{\"file\":[\"60\",\"61\",\"62\",\"63\"],\"title\":[\"\",\"\",\"\",\"\"],\"description\":[\"\",\"\",\"\",\"\"]}',0,'5',''),(18,11,'Multifunctional Tracheal Intubation','251','','',1,2,'biohermes',9,'/products/show-18.html',0,0,'127.0.0.1-54379',1716903834,1719308912,999,'&lt;p&gt;&lt;b&gt;Application:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;It is used to establish an artificial airway during clinical anesthesia or emergency treatment&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Advantages:&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;lmprove the effectiveness of connection between laryngeal hood, trachea and ventilator or anesthesia machine to ensure the stability and reliability of respiratory support&lt;/li&gt;&lt;li&gt;Tracheal intubation three-way joint with machine connection end set with threaded tube,stretched and extended without twisting&lt;/li&gt;&lt;li&gt;Tracheal intubation three-way joint set with operating hole, can be ventilated at the same time to absorb sputum and other operations, safer&lt;/li&gt;&lt;li&gt;Tracheal intubation with dosing channel, can spray drug intake channel, more convenient administration&lt;/li&gt;&lt;li&gt;Tracheal intubation three-way joint set Ruhr joint for end-of-breath carbon dioxide three-way detection&lt;/li&gt;&lt;/ul&gt;','',0,'',''),(19,11,'Disposable ETCO₂ Sampling Nasal Cannula','250','','',1,2,'biohermes',9,'/products/show-19.html',0,0,'127.0.0.1-57258',1716904174,1719307817,600,'&lt;p&gt;&lt;b&gt;Model:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;&amp;nbsp;XYG-WL2000, XYG-WL2500, XYG-WL3000, XYG-WS2000, XYG-WS2500, XYG-WS3000&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Application:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;This product is suitable for patients who need oxygen inhalation in departments including anesthesiology, emergency department, pneumology department, ICU, endoscopy center and Geriatrics department. It can monitor end-tidal carbon dioxide (ETCO2) in real-time when inhaling oxygen.&lt;/p&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Advantages:&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Innovative dual-chamber design of the breath sampling port .Double nose sampling, designed to mitigate the potential influence of the nasal cycle on CO2 sampling&lt;/li&gt;&lt;li&gt;Simultaneously delivers oxygen while sampling CO2 during spontaneous breathing. Detect the changes of patient\'s abnormal breathing timely&lt;/li&gt;&lt;li&gt;Hexagonal-structured oxygen supply cannula with an anti-pressure closure mechanism, ensuring optimal oxygen delivery&lt;/li&gt;&lt;/ul&gt;','{\"file\":[\"66\",\"67\"],\"title\":[\"\",\"\"],\"description\":[\"\",\"\"]}',0,'6',''),(20,11,'Disposable Temperature Sensor','249','','',1,2,'biohermes',9,'/products/show-20.html',0,0,'127.0.0.1-63681',1716904684,1719309467,999,'&lt;p&gt;&lt;b&gt;Model:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;Body Surface: BSTS-S-95, BSTS-M-95, BSTS-L-95&lt;/p&gt;&lt;p&gt;Body Cavity: BCTS-S-75, BCTS-M-75, BCTS-L-75, BCTS-S-85, BCTS-M-85, BCTS-L-85&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Application:&lt;/b&gt;&lt;/p&gt;&lt;p&gt;Disposable for use with compatible monitoring devices with body temperature monitoring capabilities for collecting and transmitting a patient\'s temperature signal&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;b&gt;Advantages:&lt;/b&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Single patient use, avoiding cross-infection&lt;/li&gt;&lt;li&gt;Easy to use, shorter reaction time and safer&amp;nbsp;&lt;/li&gt;&lt;li&gt;Can be used with multi-parameter monitor ssuitable for long-term continuous monitoring of patient\'s body temperature&lt;/li